const btnContact=document.querySelector(".btn-contact"),modalContainer=document.querySelector(".modal-container"),closeBtn=document.querySelector(".close-modal"),fadeElems=document.querySelectorAll(".has-animation");btnContact.addEventListener("click",function(){modalContainer.classList.remove("hidden"),document.querySelector("body").style.overflow="hidden",fadeElems.forEach(function(e){e.classList.add("smooth-visible")})}),closeBtn.addEventListener("click",function(e){modalContainer.classList.add("hidden"),document.querySelector("body").style.overflow="scroll",fadeElems.forEach(function(e){e.classList.remove("smooth-visible")})});